//states--->pass the information within the component
//declare--{}
//mutable--update the data
//two types are there 
//1.constructor
//2.without constructor method

import {Component} from "react";
class  Type extends Component{
    constructor(props){
        super(props);
        this.state={
            id:1,
            book:"react js",
            author:"john"
        }
    }
    //setState()
    change=()=>{
        this.setState({
            id:2,
            book:"java",
            author:"vijay"
        })
    }
    render(){
        return(
            <div>
            <p>
                    ID:{this.state.id}<br/>
                    Book:{this.state.book}<br/>
                    Author:{this.state.author}
            </p>
            </div>
        )
       
    }
}
export default Type;
