import './styles/cards-filter.css';
function Cardsfilter(){
    const search=()=>{
        function search(){
            const input=document.getElementById("filter").value.toUpperCase()
            const card=document.getElementsByClassName("card")
            for(let i=0;i<card.length;i++){
                var title= card[i].querySelector(".card-title")
                if(title.innerText.toUpperCase().indexOf(input)>-1){
                    card[i].style.display=""
                }
                else{
                    card[i].style.display="none"
                }
            }
        }
        console.log("search")
        const btn=document.getElementsById("add")
        const select=document.querySelector(".select")
        const n=document.querySelector("h1")
        for(var b of btn){
        b.addEventListener("click",(e)=>{
            var parent=e.target.parentNode;
            //clonenode-->it move one list to another list
            var clone=parent.cloneNode(true)
            select.appendChild(clone)
            if(clone){
                n.onclick=function(){
                    select.classList.toggle("display")
                }
            }
        })

    }
    }
        return(
        <div className='card-style'>
        <div className="card" >
            <img src="https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg"/>
            <div className="card-body">
                <h5 className="card-title">Instagram</h5>
                <p className="card-text">some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <button id='add' onClick={search}>add cart</button>
            </div>
        </div>
        
    </div>
     
    )
    
}
<div id='select'>

</div>
export default Cardsfilter;
