// States --> pass the information within the component
// {}
// mutable -- update the data 
// 2 types 
// 1`.construtor method
// 2.withput constructor method clas
// clases 

import { Component } from "react";

class App extends Component{
    constructor(props){
        super(props);
        this.state={
            id:1,
            book:"React js",
            author:"John"
        }
    }
    render(){
        return(
            <div>

                <p>
                    ID:{this.state.id}<br/>
                    Book:{this.state.book}<br/>
                    Author:{this.state.author}
                </p>

            </div>
        )
    }
}

export default App;
