// States --> pass the information within the component
// {}
// mutable -- update the data 
// 2 types 
// 1`.construtor method
// 2.withput constructor method clas
// clases 

import { Component } from "react";

class App extends Component{
    constructor(props){
        super(props);
        this.state={
            id:1,
            book:"React js",
            author:"John",
            data:0,
            value:""
        }
    }

    // setState()
   change=()=>{
    this.setState({
        id:2
       
    })
    }

    inc=()=>{
        this.setState({
            data:this.state.data + 1
        })
    }

    // onclick="herlo()"
    // onClick={this.change}

    update=(e)=>{
        this.setState({
            value:e.target.value
        })
    }



    render(){
        return(
            <div>

                <p>
                    ID:{this.state.id}<br/>
                    Book:{this.state.book}<br/>
                    Author:{this.state.author}
                </p>
                <button onClick={this.change}>Update</button>
                <br/>
                <h3>Count:{this.state.data}</h3>
                <button onClick={this.inc}>Inc</button>
                <button>Dec</button>
                <button>Reset</button>
<br/><br/>
                <input type="text" onChange={this.update}/>
                <p>Curent values:{this.state.value}</p>

               

            </div>
        )
    }
}

export default App;
